class TradingAPI {
    constructor(baseURL = 'http://localhost:8000') {
        this.baseURL = baseURL;
        this.token = null;
    }

    async login(username, password) {
        const response = await fetch(`${this.baseURL}/api/auth/login`, {
            method: 'POST',
            headers: {'Content-Type': 'application/x-www-form-urlencoded'},
            body: `username=${username}&password=${password}`
        });
        const result = await response.json();
        this.token = result.access_token;
        return result;
    }

    async getKlineData(symbol, timeframe) {
        const response = await fetch(`${this.baseURL}/api/kline/data?symbol=${symbol}&timeframe=${timeframe}`, {
            headers: {'Authorization': `Bearer ${this.token}`}
        });
        return await response.json();
    }
}

// 使用示例
const api = new TradingAPI();
api.login('testuser', 'password123').then(() => {
    return api.getKlineData('BTCUSDT', '1h');
}).then(data => {
    console.log('K线数据:', data);
});
